#Simple test for servo and angle

import time
from adafruit_servokit import ServoKit


kit = ServoKit(channels=16)

NumServo = int(input("Please enter a servo number (0 to 15) ") )

while(True):
    MonAngle = int(input("Please enter the angle to test (0 to 180) ") )
    kit.servo[NumServo].angle = MonAngle


